function saveChanges()
{
    foreach ($_POST as $setting) {
        if (!check_value($setting)) {
            message("error", "Missing data (complete all fields).");
            return NULL;
        }
    }
    $xmlPath = __PATH_MODULE_CONFIGS__ . "usercp.webshop.xml";
    $xml = simplexml_load_file($xmlPath);
    $xml->active = $_POST["active"];
    $xml->global_discount = $_POST["global_discount"];
    $xml->show_store_count = $_POST["show_store_count"];
    $xml->show_total_bought = $_POST["show_total_bought"];
    $xml->enable_platinum = $_POST["enable_platinum"];
    $xml->enable_gold = $_POST["enable_gold"];
    $xml->enable_silver = $_POST["enable_silver"];
    $xml->enable_wcoin = $_POST["enable_wcoin"];
    $xml->enable_gp = $_POST["enable_gp"];
    $xml->default_currency = $_POST["default_currency"];
    $xml->ratio_platinum = $_POST["ratio_platinum"];
    $xml->ratio_gold = $_POST["ratio_gold"];
    $xml->ratio_silver = $_POST["ratio_silver"];
