function loadExcOpts()
{
    $return = [];
    $xml = simplexml_load_file(__PATH_INCLUDES__ . "files/IGCN/ExcellentOptions.xml");
    if ($xml !== false) {
        $i = 0;
        foreach ($xml->Common->children() as $tag => $option) {
            $return["items"][$i]["ID"] = intval($option["ID"]);
            $return["items"][$i]["Number"] = intval($option["Number"]);
            $return["items"][$i]["Operator"] = intval($option["Operator"]);
            $return["items"][$i]["Value"] = intval($option["Value"]);
            $return["items"][$i]["FormulaID"] = intval($option["FormulaID"]);
            $return["items"][$i]["Name"] = strval($option["Name"]);
            $i++;
        }
        foreach ($xml->Wings->children() as $tag => $option) {
            $return["wings"][$i]["ID"] = intval($option["ID"]);
            $return["wings"][$i]["Number"] = intval($option["Number"]);
            $return["wings"][$i]["Operator"] = intval($option["Operator"]);
            $return["wings"][$i]["Value"] = intval($option["Value"]);
            $return["wings"][$i]["FormulaID"] = intval($option["FormulaID"]);
            $return["wings"][$i]["Name"] = strval($option["Name"]);
            $i++;
        }
        return $return;
    }
}

